Table.Command:
DECLARE @from DATETIME SET @from = {?$[BOY_AB_FROMDATE]} DECLARE @to DATETIME SET @to = {?$[BOY_AB_TODATE]}
DECLARE @fromDue DATETIME SET @fromDue = {?$[BOY_AB_DUE_FROMDATE]} DECLARE @toDue DATETIME SET @toDue = {?$[BOY_AB_DUE_TODATE]}
DECLARE @fromDoc DATETIME SET @fromDoc = {?$[BOY_AB_DOC_FROMDATE]} DECLARE @toDoc DATETIME SET @toDoc = {?$[BOY_AB_DOC_TODATE]}
DECLARE @cardCode NVARCHAR(15) SET @cardCode = '{?CardCode}'
DECLARE @branch INT SET @branch = {?$[BOY_AB_BRANCH]}
IF('{?$[BOY_AB_INCLUDE_RT]}' = 'N') BEGIN SELECT MAX(T0.ShortName) AS ShortName, T0.TransId AS TransId, MAX(T0.TransType) AS TransType, MAX(T0.BaseRef) AS BaseRef, MAX(T0.Ref1) AS Ref1, MAX(T0.Ref2) AS Ref2, MAX(T0.RefDate) AS RefDate, MAX(T0.DueDate) AS DueDate, MAX(T0.TaxDate) AS TaxDate, 0 AS Debit, MAX(T0.BalDueCred) + SUM(T1.ReconSum) AS Credit, 0 AS FcDebit, MAX(T0.BalFcCred) + SUM(T1.ReconSumFC) AS FcCredit, MAX(T0.LineMemo) AS LineMemo, MAX(T0.FCCurrency) AS FCCurrency FROM JDT1 T0 JOIN ITR1 T1 ON T1.TransId = T0.TransId AND T1.TransRowId = T0.Line_ID JOIN OITR T2 ON T2.ReconNum = T1.ReconNum JOIN OCRD T4 ON T4.CardCode = T0.ShortName WHERE 1=1 AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate BETWEEN @from AND @to AND T0.DueDate BETWEEN @fromDue AND @toDue AND T0.TaxDate BETWEEN @fromDoc AND @toDoc AND T4.CardCode = @cardCode AND T2.ReconDate > @to AND T1.IsCredit = 'C' GROUP BY T0.TransId, T0.Line_ID, T0.BPLName HAVING MAX(T0.BalFcCred) <>- SUM(T1.ReconSumFC) OR MAX(T0.BalDueCred) <>- SUM(T1.ReconSum)
UNION ALL
SELECT MAX(T0.ShortName), T0.TransId, MAX(T0.TransType), MAX(T0.BaseRef), MAX(T0.Ref1) AS Ref1, MAX(T0.Ref2) AS Ref2, MAX(T0.RefDate), MAX(T0.DueDate), MAX(T0.TaxDate), MAX(T0.BalDueDeb) + SUM(T1.ReconSum), 0, MAX(T0.BalFcDeb) + SUM(T1.ReconSumFC), 0, MAX(T0.LineMemo), MAX(T0.FCCurrency) FROM dbo.JDT1 T0 JOIN dbo.ITR1 T1 ON T1.TransId = T0.TransId AND T1.TransRowId = T0.Line_ID JOIN dbo.OITR T2 ON T2.ReconNum = T1.ReconNum JOIN dbo.OCRD T4 ON T4.CardCode = T0.ShortName WHERE 1=1 AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate BETWEEN @from AND @to AND T0.DueDate BETWEEN @fromDue AND @toDue AND T0.TaxDate BETWEEN @fromDoc AND @toDoc AND T4.CardCode = @CardCode AND T2.ReconDate > @to AND T1.IsCredit = 'D' GROUP BY T0.TransId, T0.Line_ID, T0.BPLName HAVING MAX(T0.BalFcDeb) <>- SUM(T1.ReconSumFC) OR MAX(T0.BalDueDeb) <>- SUM(T1.ReconSum)
UNION ALL
SELECT MAX(T0.ShortName), T0.TransId, MAX(T0.TransType), MAX(T0.BaseRef), MAX(T0.Ref1) AS Ref1, MAX(T0.Ref2) AS Ref2, MAX(T0.RefDate), MAX(T0.DueDate), MAX(T0.TaxDate), MAX(T0.BalDueDeb), MAX(T0.BalDueCred), MAX(T0.BalFcDeb), MAX(T0.BalFcCred), MAX(T0.LineMemo), MAX(T0.FCCurrency) FROM dbo.JDT1 T0 JOIN dbo.OJDT T1 ON T1.TransId = T0.TransId JOIN dbo.OCRD T2 ON T2.CardCode = T0.ShortName WHERE 1=1 AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate BETWEEN @from AND @to AND T0.DueDate BETWEEN @fromDue AND @toDue AND T0.TaxDate BETWEEN @fromDoc AND @toDoc AND T2.CardCode = @CardCode AND (T0.BalDueCred <> T0.BalDueDeb OR T0.BalFcCred <> T0.BalFcDeb ) AND NOT EXISTS ( SELECT U0.TransId, U0.TransRowId FROM dbo.ITR1 U0 JOIN dbo.OITR U1 ON U1.ReconNum = U0.ReconNum WHERE T0.TransId = U0.TransId AND T0.Line_ID = U0.TransRowId AND U1.ReconDate > @to GROUP BY U0.TransId, U0.TransRowId ) GROUP BY T0.TransId, T0.Line_ID, T0.BPLName END ELSE BEGIN SELECT T0.ShortName, T0.Debit AS Debit, T0.Credit AS Credit, T0.TransId, T0.TransType, T0.RefDate, T0.DueDate, T0.TaxDate, T0.BaseRef, T0.Ref1, T0.Ref2, T0.LineMemo, T0.FCDebit AS FcDebit, T0.FCCredit AS FcCredit, T0.FCCurrency FROM JDT1 T0 WHERE T0.shortName = @cardCode AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate BETWEEN @from AND @to AND T0.DueDate BETWEEN @fromDue AND @toDue AND T0.TaxDate BETWEEN @fromDoc AND @toDoc END

DECLARE @from DATETIME SET @from = {?$[BOY_AB_FROMDATE]} DECLARE @to DATETIME SET @to = {?$[BOY_AB_TODATE]}
DECLARE @fromDue DATETIME SET @fromDue = {?$[BOY_AB_DUE_FROMDATE]} DECLARE @toDue DATETIME SET @toDue = {?$[BOY_AB_DUE_TODATE]}
DECLARE @fromDoc DATETIME SET @fromDoc = {?$[BOY_AB_DOC_FROMDATE]} DECLARE @toDoc DATETIME SET @toDoc = {?$[BOY_AB_DOC_TODATE]}
DECLARE @cardCode NVARCHAR(15) SET @cardCode = '{?CardCode}'
DECLARE @branch INT SET @branch = {?$[BOY_AB_BRANCH]}
IF('{?$[BOY_AB_INCLUDE_RT]}' = 'N') BEGIN WITH DATA AS ( SELECT 0 AS Debit, MAX(T0.BalDueCred) + SUM(T1.ReconSum) AS Credit, 0 AS FcDebit, MAX(T0.BalFcCred) + SUM(T1.ReconSumFC) AS FcCredit FROM JDT1 T0 JOIN ITR1 T1 ON T1.TransId = T0.TransId AND T1.TransRowId = T0.Line_ID JOIN OITR T2 ON T2.ReconNum = T1.ReconNum JOIN OCRD T4 ON T4.CardCode = T0.ShortName WHERE 1=1 AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate < @from AND T4.CardCode = @cardCode AND T2.ReconDate > @to AND T1.IsCredit = 'C' GROUP BY T0.TransId, T0.Line_ID, T0.BPLName HAVING MAX(T0.BalFcCred) <>- SUM(T1.ReconSumFC) OR MAX(T0.BalDueCred) <>- SUM(T1.ReconSum)
UNION ALL
SELECT MAX(T0.BalDueDeb) + SUM(T1.ReconSum), 0, MAX(T0.BalFcDeb) + SUM(T1.ReconSumFC), 0 FROM dbo.JDT1 T0 JOIN dbo.ITR1 T1 ON T1.TransId = T0.TransId AND T1.TransRowId = T0.Line_ID JOIN dbo.OITR T2 ON T2.ReconNum = T1.ReconNum JOIN dbo.OCRD T4 ON T4.CardCode = T0.ShortName WHERE 1=1 AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate < @from AND T4.CardCode = @CardCode AND T2.ReconDate > @to AND T1.IsCredit = 'D' GROUP BY T0.TransId, T0.Line_ID, T0.BPLName HAVING MAX(T0.BalFcDeb) <>- SUM(T1.ReconSumFC) OR MAX(T0.BalDueDeb) <>- SUM(T1.ReconSum)
UNION ALL
SELECT MAX(T0.BalDueDeb), MAX(T0.BalDueCred), MAX(T0.BalFcDeb), MAX(T0.BalFcCred) FROM dbo.JDT1 T0 JOIN dbo.OJDT T1 ON T1.TransId = T0.TransId JOIN dbo.OCRD T2 ON T2.CardCode = T0.ShortName WHERE 1=1 AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate < @from AND T2.CardCode = @CardCode AND (T0.BalDueCred <> T0.BalDueDeb OR T0.BalFcCred <> T0.BalFcDeb ) AND NOT EXISTS ( SELECT U0.TransId, U0.TransRowId FROM dbo.ITR1 U0 JOIN dbo.OITR U1 ON U1.ReconNum = U0.ReconNum WHERE T0.TransId = U0.TransId AND T0.Line_ID = U0.TransRowId AND U1.ReconDate > @to GROUP BY U0.TransId, U0.TransRowId ) GROUP BY T0.TransId, T0.Line_ID, T0.BPLName ) SELECT SUM(T0.Debit-T0.Credit) AS Primo, SUM(T0.FcDebit-T0.FcCredit) AS PrimoFc FROM DATA T0 END ELSE BEGIN SELECT SUM(T0.Debit-T0.Credit) AS Primo, SUM(T0.FCDebit-T0.FCCredit) AS PrimoFc FROM JDT1 T0 WHERE T0.shortName = @cardCode AND CASE WHEN @branch = -1 THEN -1 ELSE T0.BPLId END = @branch AND T0.RefDate < ( SELECT MIN(T0.RefDate) FROM JDT1 T0 WHERE T0.shortName = @cardCode AND T0.RefDate BETWEEN @from AND @to AND T0.DueDate BETWEEN @fromDue AND @toDue AND T0.TaxDate BETWEEN @fromDoc AND @toDoc ) END
